import React, { Component } from "react";
import "./assets/styles/main.scss";
import API from "./api/axios";
import ListMembers from "./ListMembers";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      member: "",
      listMembers: [],
    };
  }

  async componentDidMount() {
    const listMembers = await API.get("members");

    this.setState({ listMembers });
  }

  updateData = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState({ [name]: value });
  };

  addMember = async (e) => {
    e.preventDefault();
    const member = this.state.member;

    const body = {
      member,
    };

    try {
      await API.post("members", body);
    } catch (err) {
      console.log(err);
    }
  };
  render() {
    const { listMembers } = this.state;
    console.log(this.state.listMembers);
    return (
      <>
        <section className="top">
          <form className="form">
            <div className="title">
              <h1>Add an Argonaut</h1>
            </div>
            <div className="form__container">
              <div className="form__registration">
                <p className="form__subtext">Name</p>
                <input
                  name="member"
                  onChange={this.updateData}
                  className="form__input"
                />
              </div>
              <button
                onClick={this.addMember}
                type="submit"
                className="form__button"
              >
                Send
              </button>
            </div>
          </form>
        </section>
        <section className="bottom">
          <div className="title">
            <h1>Crew Members</h1>
          </div>
          <div>
            <div className="columns">
              <img className="columns__items" src="./col.png"></img>
              <img className="columns__items" src="./col.png"></img>
              <img className="columns__items" src="./col.png"></img>
            </div>
          </div>
          <div className="name-container">
            {listMembers.map((props, index) => {
              <ListMembers key={index} member={props.member} />;
            })}
          </div>
        </section>
        <footer></footer>
      </>
    );
  }
}

export default App;
